import React from 'react';

class addRecipe extends React.Component {
	
	constructor(props){
		super(props);
		this.state = {
			recipe_name : "",
			description : "",
			category : "",
			country : '',
			incredients : [],
			cooking_time : "",
			type : "",
			n_type : '',
			tips : [],
			timing : "",
			image_name : "",
			video_links : ''
		}
	}

	handleChange(event){
		let name = event.target.name;
		this.setState({
			[name]  : event.target.value
		})
	}

	handleSubmit(event){
		event.preventDefault();
		console.log(this.state);
	}

	render(){
		return(
			<>
			<div className='container-fluid'>
				<form onSubmit={ this.handleSubmit.bind(event) } style={{margin : 'auto'}} >
					<div style={{ marginTop : '30px' }} className=" form-group row">
						<label className="col-sm-2 col-form-label">Country Name</label>
						<div className="col-sm-6">
							<div className="input-group">
								<input type="text" className='form-control' onChange={this.handleChange.bind(this)} name="recipe_name" value={this.state.recipe_name} placeholder="Recipe Name" />
					        </div>
				        </div>
				    </div>
			        <br />
				    <div className="form-group row">
					    <div className="col-sm-6" style={{ margin:'auto' }}>
					      <input type="submit" className="btn btn-outline-primary" />
					    </div>
					</div>
			    </form>
				<form>
					<label>Recipe Name</label>
					
					<label>Recipe Name</label>
					<input type="text" onChange={this.handleChange.bind(this)} name="description" value={this.state.description} placeholder="Recipe Name" />
				</form>
			</div>
			</>
		)
	}
}

export default addRecipe;
