import React from 'react';
// import axios from '../../axios';

import './css/category.css'

// const url = window.location.href;
// let id = url.split("/");
// id = id[5];

class EditCategory extends React.Component{


	constructor(props){
		super(props);
		this.state = {
			data : [],
			cat_name : ["New Category"],
			questions: ["test1","test2","test2","test4"]
		}
	}

	componentDidMount(){
		// axios.get(`/category/edit/${id}`).then((result)=>{
		// 	if(result.data.status){
		// 		this.setState({
		// 			data : result.data.data
		// 		})
		// 	} else {
		// 		console.log(result.data.data)
		// 	}
		// }).catch((err)=>{
		// 	console.log(err);
		// })
	}

	handleSubmit(event){
		event.preventDefault();
		let index_of_empty_string = this.state.questions.indexOf("");
		if(this.state.cat_name[0] !== "" && index_of_empty_string === -1){
			let data = {
				category_name : this.state.cat_name[0],
				sub_category_name : this.state.questions	
			}
			console.log(data);
		}
	}

	handleText = i => e => {
	    let questions = [...this.state.questions]
	    questions[i] = e.target.value
	    this.setState({
	      questions
	    })
	}

	handleCatName = i => e => {
	    let questions = [...this.state.cat_name]
	    questions[0] = e.target.value
	    this.setState({
	      cat_name : questions
	    })
	}

	handleDelete = i => e => {
	    e.preventDefault();
	    console.log("called");
	    let questions = [
	      ...this.state.questions.slice(0, i),
	      ...this.state.questions.slice(i + 1)
	    ]
	    this.setState({
	      questions
	    })
	}

	addQuestion = e => {
	    e.preventDefault()
	    let questions = this.state.questions.concat([''])
	    this.setState({
	      questions
	    })
	}

	render(){
		return(
			<>
		    <div className="container">
			  <form action="/action_page.php">
			    <div className="row">
			      <div className="col-25">
			        <label htmlFor="fname">First Name</label>
			      </div>
			      <div className="col-75">
			        <input type="text" id="fname" name="firstname" placeholder="Your name.."/>
			      </div>
			    </div>
			    <div className="row">
			      <div className="col-25">
			        <label htmlFor="lname">Last Name</label>
			      </div>
			      <div className="col-75">
			        <input type="text" id="lname" name="lastname" placeholder="Your last name.."/>
			      </div>
			    </div>
			    <div className="row">
			      <div className="col-25">
			        <label htmlFor="country">Country</label>
			      </div>
			      <div className="col-75">
			        <select id="country" name="country">
			          <option value="australia">Australia</option>
			          <option value="canada">Canada</option>
			          <option value="usa">USA</option>
			        </select>
			      </div>
			    </div>
			    <div className="row">
			      <div className="col-25">
			        <label htmlFor="subject">Subject</label>
			      </div>
			      <div className="col-75">
			        <textarea id="subject" name="subject" placeholder="Write something.." style={{height:'200px'}}></textarea>
			      </div>
			    </div>
			    <div className="row">
			      <input type="submit" value="Submit" />
			    </div>
			  </form>
			</div>
			</>
		)
	}
}

export default EditCategory
