import React from 'react';

class AddCategory extends React.Component {

	constructor(props){
		super(props);
		this.state = {
			country_name : "",
			country_image : null,
			country_name_error : ""
		}
	}

	handleSubmit(event){
		event.preventDefault();
	}

	render(){
		return(
			<>
			<div className="container">
				<form onSubmit={ this.handleSubmit.bind(this) }>
					<div style={{ marginTop : '30px' }} className=" form-group row">
						<label className="col-sm-2 col-form-label">Categoty Name</label>
						<div className="col-sm-6">
							<div className="input-group">
								<input
						            type="text"
						            onChange={this.handleCatName(0)}
						            value={this.state.cat_name}
						            className ="form-control"
						        />
					        </div>
				        	<p style={{color:"red",margin : 'auto auto auto auto'}} >{this.state.country_name_error}</p>
				        </div>
				    </div>
			        <br />
				    <div className="form-group row">
					    <div className="col-sm-6" style={{ margin:'auto' }}>
					      <input type="submit" className="btn btn-outline-primary" />
					    </div>
					</div>
			    </form>
		    </div>
			</>
		)
	}
}

export default AddCategory;
