import React from 'react';

class addShop extends React.Component  {

	constructor(props){
		super(props);
		this.state = {
			id : ""	
		}
	}

	componentDidMount(){
		let params = new URLSearchParams(window.location.search).searchParams;
		console.log(params);
		this.setState({
			id : params
		})
	}

	render() {
		return (
			<>
				<center>{ this.state.id }</center>
			</>
		)
	}
}

export default addShop;
