module.exports = {
	validate_country_form(data) {
		let error = {
			country_name_error : '',
			country_image_error : ''
		}
		let valid = true;
		if( data[0] === '' ){
			error['country_name_error'] = 'Filed must not be empty';
			valid = false;
		} 
		if( data[1] === null ) {
			error['country_image_error'] = 'Filed must not be empty';;
			valid = false;
		}
		if(valid) return {}
		return error;
	}
}
