import React from 'react';
import axios from '../../axios';

class addProduct extends React.Component {

	constructor(props){
		super(props);
		this.state = {
			response : [],
			product_name : '',
			main_category : '',
			sub_category : '',
			description : '',
			quantity : '',
			total_available_count  : '',
		}
	}

	componentDidMount(){
		axios.get('/category/get_category').then((response)=>{
			if(response.data.data.length > 0){
				this.setState({
					response : response.data.data
				})
			}
		}).catch((err)=>{
			console.log(err);
		})
	}

	render(){
		return(
			<>
			<div className='container-fluid'>

				<div className=" form-group row">
						<label className="col-sm-2 col-form-label">Recipe Type</label>
						<div className="col-sm-6">
							<div className="input-group">
								<select title="Recipe Category" name="category" onChange = { this.handleChange.bind(this) } className="form-control">
								  	<option value=''>Category</option>
								  	<option value="Briyani">Briyani</option>
						            <option value="Noodles">Noodles</option>
						            <option value="Rice">Rice</option>
						            <option value="Salad">Salad</option>
								</select>
					        </div>
				        </div>
				    </div>


				{
					this.state.response.map((data,index)=>
						<h6 key={index}>{ data['category_name'] }</h6>
					)
				}
			</div>
			</>
		)
	}
}

export default addProduct;
