import React from 'react';
import axios from '../../axios';

class addProduct extends React.Component {

	constructor(props){
		super(props);
		this.state = {
			response : [],
			sub_category_response : [],
			product_name : '',
			main_category : '',
			main_category_id : '',
			sub_category : '',
			description : '',
			quantity : '',
			total_available_count  : '',
			disabled : "disabled"
		}
	}

	componentDidMount(){
		axios.get('/category/get_category').then((response)=>{
			if(response.data.data.length > 0){
				this.setState({
					response : response.data.data
				})
			}
		}).catch((err)=>{
			console.log(err);
		})
	}

	handleChange(event){
		if(event.target.name === 'main_category'){
			if(event.target.value !== ''){
				axios.get('/category/sub_cat_name/'+event.target.value).then((response)=>{
					if(response.data.data[0]['sub_category'].length > 0){
						this.setState({
							disabled : "",
							sub_category_response : response.data.data[0]['sub_category']
						})
					}
				}).catch((err)=>{
					console.log(err);
				})
				
			} else {
				this.setState({
					disabled : "disabled",
					sub_category_response : []
				})
			}
		} else {
			let name = event.target.name;
			this.setState({
				[name] : event.target.value
			})
		}
	}

	render(){
		return(
			<>
			<div style={{ marginTop : '30px' }} className='container'>
				<div className=" form-group row">
					<label className="col-sm-2 col-form-label">Product Type</label>
					<div className="col-sm-6">
						<div className="input-group">
							<select onChange={ this.handleChange.bind(this) } title="Product Type" name="main_category"  className="form-control">
							  	<option data-id='' value=''>Select the main category</option>
							  	{
									this.state.response.map((data,index)=>
										<option key={ index } value={data['_id']}>{data['category_name']}</option>
									)
								}
							</select>
				        </div>
			        </div>
			    </div>
			    <div className=" form-group row">
					<label className="col-sm-2 col-form-label">Product Sub Type</label>
					<div className="col-sm-6">
						<div className="input-group">
							<select disabled = { this.state.disabled } onChange={ this.handleChange.bind(this) } title="Product Type" name="main_category"  className="form-control">
							  	<option value=''>Select the sub category</option>
							  	{
									this.state.sub_category_response.map((data,index)=>
										<option key={ index } data-id={ data['_id'] } value={data['category_name']}>{data['category_name']}</option>
									)
								}
							</select>
				        </div>
			        </div>
			    </div>
			</div>
			</>
		)
	}
}

export default addProduct;
