import React from 'react';
import axios from '../../axios';

class ManageCategory extends React.Component{

	constructor(props) {
	    super(props);
	    this.state = {
	    	res : []
	    }
	}

	componentDidMount(){
		axios.get('/category').then((result) => {
			if(result.data.status !== 0){
				this.setState({
					res : result.data.data
				});
				console.log(this.state.res);
			} else {
				console.log(result.data.data)
			}
		}).catch((err) => {
			console.log(err);
		})
	}

	render(){
		return(
			<>
			<div className="container-fluid">
				<hr/>
				<div className="col-2">
				</div>
				<div className="col-10">
				<div className="table-responsive">
					<table className="table table-bordered table-hover" style = {{ textAlign : 'center' ,backgroundColor : 'white' }}>
					  <thead>
					    <tr style={{ color : "#467339" }}>
					      <th scope="col">S.No</th>
					      <th scope="col">Category Name</th>
					      <th scope="col">Subcategoty</th>
					      <th scope="col">Action</th>
					    </tr>
					  </thead>
					  <tbody>
					  {
					    this.state.res.map((data,i) => 
		                    <tr key={i}>
		                    	<td>{1}</td>
		                        <td>{data['category_name']}</td>
		                        <td>{data['sub_category'].join(",")}</td>
		                        <td>
		                        <div className="btn-group" role="group" aria-label="Basic example">
								  <button type="button" title="Edit" className="btn btn-outline-primary btn-sm"><i class="fa fa-edit"></i></button>&nbsp;
								  <button type="button" title="Block" className="btn btn-outline-info btn-sm"><i class="fa fa-ban"></i></button>&nbsp;
								  <button type="button" title= "Delete" className="btn btn-outline-danger btn-sm"><i class="fa fa-trash"></i></button>
								</div>
		                        </td>
		                   	</tr>
						)
					  }
					  </tbody>
					</table>
				</div>
				</div>
			</div>
			</>
		)
	}
}

export default ManageCategory;
