import React from 'react';

class addShop extends React.Component  {

	constructor(props){
		super(props);
		this.state = {
			id : ""	
		}
	}

	componentDidMount(){
		const urlParams = new URLSearchParams(window.location.add_shop);
		const myParam = urlParams.get('id');
		this.setState({
			id : myParam
		})
	}

	render() {
		return (
			<>
				<center>Add Shop</center>
			</>
		)
	}
}

export default addShop;
