import React from 'react';
import axios from '../../axios';

class addProduct extends React.Component {

	constructor(props){
		super(props);
		this.state = {
			response : [],
			product_name : '',
			main_category : '',
			sub_category : '',
			description : '',
			quantity : '',
			total_available_count  : '',
		}
	}

	componentDidMount(){
		axios.get('/category/get_category').then((response)=>{
			if(response.data.data.length > 0){
				this.setState({
					response : response.data.data
				})
			}
		}).catch((err)=>{
			console.log(err);
		})
	}

	handleChange(event){
		console.log(event.target.value);
	}

	render(){
		return(
			<>
			<div style={{ marginTop : '30px' }} className='container'>
				<div className=" form-group row">
					<label className="col-sm-2 col-form-label">Recipe Type</label>
					<div className="col-sm-6">
						<div className="input-group">
							<select onChange={ this.handleChange.bind(this) } title="Recipe Category" name="category"  className="form-control">
							  	<option value=''>Select the main category</option>
							  	{
									this.state.response.map((data,index)=>
										<option value={data['_id']}>{data['category_name']}</option>
									)
								}
							</select>
				        </div>
			        </div>
			    </div>
			</div>
			</>
		)
	}
}

export default addProduct;
