import React from 'react';

class addRecipe extends React.Component {
	
	constructor(props){
		super(props);
		this.state = {
			recipe_name : "",
			description : "",
			category : "",
			country : '',
			incredients : [],
			cooking_time : "",
			type : "",
			n_type : '',
			tips : [],
			process: [""],
			precess_error:[""],
			timing : "",
			image_name : "",
			video_links : ''
		}
	}

	componentDidMount(){
		console.log("Make an api call to get the details like country and cousines list and more");
	}

	handleChange(event){
		let name = event.target.name;
		this.setState({
			[name]  : event.target.value
		})
	}

	handleSubmit(event){
		event.preventDefault();
		console.log(this.state);
	}

	addQuestion = e => {
	    e.preventDefault();
	    if(this.state.process.indexOf("") === -1){
		    let process_filed = this.state.process.concat([''])
		    let process_filed_error = this.state.process_filed_error.concat([''])
		    this.setState({
		      process_filed,
		      process_filed_error
		    })
	    } else {
	    	// this.setState({
	    	// 	custom_interval_error : "Please Fill The Fileds To Add More"
	    	// })
	    }
	}

	handleDelete = i => e => {
	    e.preventDefault();
	    let sub_category_name = [
	      ...this.state.process.slice(0, i),
	      ...this.state.process.slice(i + 1)
	    ]
	   	let sub_category_error = [
	      ...this.state.precess_error.slice(0, i),
	      ...this.state.precess_error.slice(i + 1)
	    ]
	    this.setState({
	      sub_category_name,
	      sub_category_error
	    })
	}

	handleText = i => e => {
	    let process_filed = [...this.state.process]
	    process_filed[i] = e.target.value
	    this.setState({
	      sub_category_name
	    })
	}


	render(){
		return(
			<>
			<div className='container'>
				<form onSubmit={ this.handleSubmit.bind(this) } >
					<div style={{ marginTop : '30px' }} className=" form-group row">
						<label className="col-sm-2 col-form-label">Recipe Name</label>
						<div className="col-sm-6">
							<div className="input-group">
								<input type="text" className='form-control' onChange={this.handleChange.bind(this)} name="recipe_name" value={this.state.recipe_name} placeholder="Recipe Name" />
					        </div>
				        </div>
				    </div>
				    <div className=" form-group row">
						<label className="col-sm-2 col-form-label">Cousine Country</label>
						<div className="col-sm-6">
							<div className="input-group">
								<select name="country" onChange = { this.handleChange.bind(this) } className="form-control">
								  	<option value=''>Default select</option>
								  	<option value="grapefruit">India</option>
						            <option value="lime">Spine</option>
						            <option value="coconut">Mexcican</option>
						            <option value="mango">Italian</option>
								</select>
					        </div>
				        </div>
				    </div>
				    <div className=" form-group row">
						<label className="col-sm-2 col-form-label">Recipe Type</label>
						<div className="col-sm-6">
							<div className="input-group">
								<select name="category" onChange = { this.handleChange.bind(this) } className="form-control">
								  	<option value=''>Default select</option>
								  	<option value="grapefruit">Briyani</option>
						            <option value="lime">Noodles</option>
						            <option value="coconut">Rice</option>
						            <option value="mango">Salad</option>
								</select>
					        </div>
				        </div>
				    </div>
				    {this.state.process.map((question, index) => (
			          <div className=" form-group row" key={index}>
			          	<label className="col-sm-2 col-form-label">Sub Categoty Name </label>
			            <div className="col-sm-6">
				            <div className="input-group">
					            <input
					              type="text"
					              onChange={this.handleText(index)}
					              value={question}
					              className ="form-control"
					            />&nbsp;&nbsp;
				            	index == 0 ? <button style={{ fontSize : '18px' }} title="Add Category" className="btn btn-outline-success btn-sm" onClick={this.addQuestion}>+</button> : <button title="Remove" className="btn btn-outline-danger btn-sm" onClick={this.handleDelete(index)}>X</button>
				            </div>
			          		<p style={{color:"red",margin : 'auto auto auto auto'}} >{ this.state.precess_error[index] }</p>
			          	</div>
			          </div>
			        ))}
				    <div className=" form-group row">
						<label className="col-sm-2 col-form-label">Recipe Description</label>
						<div className="col-sm-6">
							<div className="input-group">
								<textarea className='form-control' name='description' onChange={this.handleChange.bind(this)} placeholder="How to make this..."></textarea>
					        </div>
				        </div>
				    </div>
			        <br />
				    <div className="form-group row">
					    <div className="col-sm-6" style={{ margin:'auto' }}>
					      <input type="submit" className="btn btn-outline-primary" />
					    </div>
					</div>
			    </form>
			</div>
			</>
		)
	}
}

export default addRecipe;
