import React from 'react';
import { Link } from 'react-router-dom';

class manageRecipe extends React.Component {

	constructor(props){
		super(props);
		this.state = {
			data : [{
				_id : '212871289472384733',
				recipe_name : "chicken Briyani",
				description : "Lorem Ipsam Lorem Ipsam Lorem Ipsam Lorem Ipsam Lorem Ipsam Lorem Ipsam Lorem Ipsam",
				category : "Briyani",
				country : 'India',
				incredients : [
					"rice",
					'chicken',
					'masala',
					'onion',
					'garlic',
					'etc',
				],
				status : "A",
				cooking_time : "30 mins",
				type : "Non_veg",
				n_type : 'chicken',
				tips : ["Lorem Lorem Lorem"]
			}]
		}
	}
	render(){
		return(
			<>
			<div className = "container-fluid">
				<hr />
				<Link className="btn btn-outline-primary btn-sm" to={'/recipe/add_recipe'}>Add Recipe</Link>
				<hr/>
				<div className="table-responsive">
					<table className="table table-bordered table-hover" style = {{ textAlign : 'center' ,backgroundColor : '#E3F2FD' }}>
						<thead>
							<tr>
								<th>Recipe Name</th>
								<th>Cousine</th>
								<th>Type</th>
								<th>Categort</th>
								<th>Incredients</th>
								<th>Cooking Time</th>
								<th>status</th>
								<th>Action</th>
							</tr>
						</thead>
						<tbody>
							{
								this.state.data.map((item , i) =>
									<tr key={i}>
			                    		<td>{i+1}</td>
			                    	</tr>
								)
							}
						</tbody>
					</table>
				</div>
			</div>
			</>
		)
	}
}

export default manageRecipe;
