import React from 'react';
import axios from '../../axios'

class ManageCategory extends React.Component{

	constructor(props) {
	    super(props);
	    this.state = {
	    	data : []
	    }
	}

	componentDidMount(){
		axios.get('/category').then(function(result){
			console.log(result.data.data);
			if(result.status !== 0){
				this.setState({
					data : result.data.data
				})
			}
		}).catch(function(err){
			console.log(err);
		})
	}

	render(){
		return(
			<>
				<div className="container-fluid">
				<hr/>
				<div className="table-responsive">
					<table className="table table-bordered table-hover" style = {{ textAlign : 'center' ,backgroundColor : '#E3F2FD' }}>
					  <thead>
					    <tr>
					      <th scope="col">S.No</th>
					      <th scope="col">Category Name</th>
					      <th scope="col">Subcategoty</th>
					      <th scope="col">Action</th>
					    </tr>
					  </thead>
					  <tbody>
					    <tr>
					      <th scope="row">1</th>
					      <td>Mark</td>
					      <td>Otto</td>
					      <td>@mdo</td>
					    </tr>
					    <tr>
					      <th scope="row">2</th>
					      <td>Jacob</td>
					      <td>Thornton</td>
					      <td>@fat</td>
					    </tr>
					    <tr>
					      <th scope="row">3</th>
					      <td>Larry</td>
					      <td>the Bird</td>
					      <td>@twitter</td>
					    </tr>
					  </tbody>
					</table>
				</div>
				</div>
			</>
		)
	}
}

export default ManageCategory;
