import React from 'react';
import { Link } from 'react-router-dom';

class manageRecipe extends React.Component {

	constructor(props){
		super(props);
		this.state = {
			data : [{
				_id : '212871289472384733',
				recipe_name : "chicken Briyani",
				description : "Lorem Ipsam Lorem Ipsam Lorem Ipsam Lorem Ipsam Lorem Ipsam Lorem Ipsam Lorem Ipsam",
				category : "Briyani",
				country : 'India',
				incredients : [
					"rice",
					'chicken',
					'masala',
					'onion',
					'garlic',
					'etc',
				],
				status : "A",
				cooking_time : "30 mins",
				type : "Non_veg",
				n_type : 'chicken',
				tips : ["Lorem Lorem Lorem"]
			}]
		}
	}
	render(){
		return(
			<>
			<div className = "container-fluid">
				<hr />
				<Link className="btn btn-outline-primary btn-sm" to={'/recipe/add_recipe'}>Add Recipe</Link>
				<hr/>
				<table className="table-responsive">
					<thead>
						<tr>Recipe Name</tr>
						<tr>Cousine</tr>
						<tr>Type</tr>
						<tr>Categort</tr>
						<tr>Incredients</tr>
						<tr>Cooking Time</tr>
						<tr>status</tr>
						<tr>Action</tr>
					</thead>
					<tbody>
						{
							this.state.data.map((item , i)=>{
								<tr key={i}>
		                    		<td>{i+1}</td>
		                    	</tr>
							})
						}
					</tbody>
				</table>
			</div>
			</>
		)
	}
}

export default manageRecipe;
