import React from 'react';

class addRecipe extends React.Component {
	
	constructor(props){
		super(props);
		this.state = {
			recipe_name : "",
			description : "",
			category : "",
			country : '',
			incredients : [],
			cooking_time : "",
			type : "",
			n_type : '',
			tips : [],
			timing : "",
			image_name : "",
			video_links : ''
		}
	}

	handleChange(event){
		console.log(event.target);
		let formData = [...this.state];
		formData[event.target.name] = event.target.value
		this.setState({
			formData 
		})
		console.log(this.state);
	}

	render(){
		return(
			<>
			<div className='container-fluid'>
				<form>
					<label>Recipe Name</label>
					<input type="text" onChange={this.handleChange.bind(this)} name="recipe_name" value={this.state.recipe_name} placeholder="Recipe Name" />
					<label>Recipe Name</label>
					<input type="text" onChange={this.handleChange.bind(this)} name="description" value={this.state.description} placeholder="Recipe Name" />
				</form>
			</div>
			</>
		)
	}
}

export default addRecipe;
