import React from 'react';
import axios from '../../axios';

const url = window.location.href;
let id = url.split("/");
id = id[5];

class EditCategory extends React.Component{


	constructor(props){
		super(props);
		this.state = {
			data : [],
			cat_name : [""],
			questions: [""]
		}
	}

	componentDidMount(){
		axios.get(`/category/edit/${id}`).then((result)=>{
			if(result.data.status){
				this.setState({
					data : result.data.data
				})
			} else {
				console.log(result.data.data)
			}
		}).catch((err)=>{
			console.log(err);
		})
	}

	handleSubmit(event){
		event.preventDefault();
		let index_of_empty_string = this.state.questions.indexOf("");
		if(this.state.cat_name[0] !== "" && index_of_empty_string == -1){
			let data = {
				category_name : this.state.cat_name[0],
				sub_category_name : this.state.questions	
			}
			console.log(data);
		}
	}

	handleText = i => e => {
	    let questions = [...this.state.questions]
	    questions[i] = e.target.value
	    this.setState({
	      questions
	    })
	}

	handleCatName = i => e => {
	    let questions = [...this.state.cat_name]
	    questions[0] = e.target.value
	    this.setState({
	      cat_name : questions
	    })
	}

	handleDelete = i => e => {
	    e.preventDefault()
	    let questions = [
	      ...this.state.questions.slice(0, i),
	      ...this.state.questions.slice(i + 1)
	    ]
	    this.setState({
	      questions
	    })
	}

	addQuestion = e => {
	    e.preventDefault()
	    let questions = this.state.questions.concat([''])
	    this.setState({
	      questions
	    })
	}

	render(){
		return(
			<>

			<form>
			  <div className="form-row">
			    <div className="col-7">
			      <input type="text" className="form-control" placeholder="City"/>
			    </div>
			    <div className="col">
			      <input type="text" className="form-control" placeholder="State"/>
			    </div>
			    <div className="col">
			      <input type="text" className="form-control" placeholder="Zip"/>
			    </div>
			  </div>
			</form>







			<div className="container">
			<form onSubmit={ this.handleSubmit.bind(this) }>
				<div className="form-row">
					<div classNAme="col-7">
						<label>Categoty Name </label>
						<input
				            type="text"
				            onChange={this.handleCatName(0)}
				            value={this.state.cat_name[0]}
				            className ="form-control"
				        />
				        <button onClick={this.addQuestion}>Add Sub Category</button>
			        </div>
		        {this.state.questions.map((question, index) => (
		          <div classNAme="col-md-4 mb-3" key={index}>
		          	<label>Sub Categoty Name </label>
		            <input
		              type="text"
		              onChange={this.handleText(index)}
		              value={question}
		              className ="form-control"
		            />
		            <button onClick={this.handleDelete(index)}>X</button>
		          </div>
		        ))}
		        <br />
		        <input type="submit" />
		        </div>
		    </form>
		    </div>
			</>
		)
	}
}

export default EditCategory
